<div class="product-quick-view">
    <div class="container">
        <div class="modal fade" id="product_quick_view">
            <div class="modal-dialog modal-lg modal-dialog-centered">
                <div class="modal-content">
                    <div class="modal-header">
                        <button type="button" class="close" data-dismiss="modal">&times;</button>
                    </div>

                    <div class="modal-body">
                        <div class="row">
                            <div class="col-12 col-md-6 col-lg-7">
                                <div class="product-gallery">
                                    <div class="gallery-with-thumbs" data-nav-center="false">
                                        <div class="product-image-container">
                                            <div class="product-full-image main-slider image-popup">
                                                <div class="swiper-wrapper api-component--quickview-swiper_slide"></div>
                                            </div>
                                        </div>
                                        
                                        <div class="product-thumb-container">
                                            <div class="product-thumb-image pos-r">
                                                <div class="nav-slider">
                                                    <!-- Slides -->
                                                    <div class="api-component--quickview-thumbnails"></div>

                                                    <div class="swiper-arrow next"><i class="fa fa-angle-down"></i></div>
                                                    <div class="swiper-arrow prev"><i class="fa fa-angle-up"></i></div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="col-12 col-md-6 col-lg-5">
                                <div class="product-details">
                                    <h3 class="product-name api-component--quickview-name"></h3>
                                    <div class="product-ratings">
                                        <ul class="rating d-flex">
                                            <li><i class="ion ion-md-star"></i></li>
                                            <li><i class="ion ion-md-star"></i></li>
                                            <li><i class="ion ion-md-star-outline disabled"></i></li>
                                            <li><i class="ion ion-md-star-outline disabled"></i></li>
                                            <li><i class="ion ion-md-star-outline disabled"></i></li>
                                        </ul>
                                    </div>
                                    <div class="product-price api-component--quickview-product_price"></div>
                                    <div class="product-description api-component--quickview-product_description"></div>
                                    <form action="#" class="product-actions">
                                        <h3>Available Options</h3>
                                        <div class="product-size-color d-flex">
                                            <div class="product-size">
                                                <label>Size</label>
                                                <select class="nice-select">
                                                    <option>S</option>
                                                    <option>M</option>
                                                    <option>L</option>
                                                </select>
                                            </div>
                                            <div class="product-color">
                                                <label>color</label>
                                                <ul class="color-list">
                                                   <li>
                                                        <a class="white" href="#"></a>
                                                    </li>
                                                    <li>
                                                        <a class="orange active" href="#"></a>
                                                    </li>
                                                    <li>
                                                        <a class="paste" href="#"></a>
                                                    </li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div class="api-component--quickview-actions"></div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>